import numpy as np
from bootstrap_stderr import num_resamples


def least_squares(x, y):
    x_mean = np.sum(x)/len(x)
    y_mean = np.sum(y)/len(y)
    x_dev = x - x_mean
    y_dev = y - y_mean
    a1 = np.sum(x_dev * y_dev) / np.sum(np.square(x_dev))
    a0 = y_mean - (a1 * x_mean)
    return a0, a1


